INSERT INTO `wp_posts` VALUES("439", "3", "2012-11-09 13:56:49", "2012-11-09 13:56:49", "Giorno 27 Ottobre 2012, si è tenuta la prima riunione della società A.S.D. Pro Ragusa.\r\n\r\nAlla presenza del Presidente Giorgio Mirabella, del Mister Salvatore Schininà, del preparatore atletico Carmelo Scalone e l\'amichevole partecipazione di Ciccio Barone si sono riuniti per la prima volta tutti i giocatori che prenderanno parte a questa nuova avventura che sta per iniziare.\r\n\r\n&nbsp;", "Prima Riunione A.S.D. Pro Ragusa", "", "trash", "open", "open", "", "riunione-2", "", "", "2013-10-08 18:34:46", "2013-10-08 16:34:46", "", "0", "http://www.asdproragusa.it/?p=439", "0", "post", "", "8593");
