INSERT INTO `wp_posts` VALUES("796", "1", "2012-11-21 15:26:14", "2012-11-21 15:26:14", "<h1>Presentazione Ufficiale</h1>\r\nIl Presidente Giorgio Mirabella e lo Staff sono lieti di invitarvi alla presentazione ufficiale della società A.S.D. Pro Ragusa.\r\nIl tutto si svolgerà presso i locali della pizzeria [themecolor]\"La Giara\"[/themecolor] in Viale delle Americhe, 183, 97100 Ragusa.\r\n\r\nAlle ore [themecolor]21.00[/themecolor] verrà presentata la squadra alla stampa e ai tifosi presenti, seguirà un rinfresco .\r\n\r\nVi attendiamo numerosi.\r\n\r\n[themecolor]Lo Staff [/themecolor]\r\n<h1></h1>\r\n<h2>Dove Trovarci</h2>\r\n<iframe src=\"https://maps.google.it/maps?source=embed&amp;hl=it&amp;ie=UTF8&amp;q=la+giara+ragusa&amp;fb=1&amp;gl=it&amp;hq=la+giara&amp;hnear=0x131199adbb2e54a9:0x27e898810ccef3ad,Ragusa+RG&amp;cid=0,0,15304441808009745116&amp;ll=36.932948,14.690416&amp;spn=0.006003,0.013733&amp;t=m&amp;layer=c&amp;cbll=36.930718,14.69039&amp;panoid=YpmYiqWpOjCM8DzIg6RdzQ&amp;cbp=13,355.79,,0,-3.06&amp;output=svembed\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" width=\"425\" height=\"350\"></iframe>\r\n\r\n&nbsp;\r\n\r\n<iframe src=\"https://maps.google.it/maps?hl=it&amp;ie=UTF8&amp;q=la+giara+ragusa&amp;fb=1&amp;gl=it&amp;hq=la+giara&amp;hnear=0x131199adbb2e54a9:0x27e898810ccef3ad,Ragusa+RG&amp;cid=0,0,15304441808009745116&amp;t=m&amp;ll=36.932604,14.690416&amp;spn=0.006003,0.009141&amp;z=16&amp;iwloc=A&amp;output=embed\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" width=\"425\" height=\"350\"></iframe>", "Presentazione", "", "inherit", "open", "open", "", "771-revision-23", "", "", "2012-11-21 15:26:14", "2012-11-21 15:26:14", "", "771", "http://www.asdproragusa.it/771-revision-23/", "0", "revision", "", "0");
