INSERT INTO `wp_posts` VALUES("1458", "1", "2013-10-08 18:34:46", "2013-10-08 16:34:46", "<h1>Presentazione Ufficiale</h1>\r\nIl Presidente Giorgio Mirabella e lo Staff sono lieti di invitarvi alla presentazione ufficiale della società A.S.D. Pro Ragusa.\r\nIl tutto si svolgerà presso i locali della pizzeria [themecolor]\"La Giara\"[/themecolor] in Viale delle Americhe, 183, 97100 Ragusa.\r\n\r\nAlle ore [themecolor]21.00[/themecolor] verrà presentata la squadra alla stampa e ai tifosi presenti, seguirà un rinfresco .\r\n\r\nVi attendiamo numerosi.\r\n\r\nPer Info contattare [themecolor]Vito Ferlito[/themecolor] o inviare una mail a <a href=\"mailto:info@asdproragusa.it\">info@asdproragusa.it</a>\r\n\r\n[themecolor]Lo Staff [/themecolor]\r\n<h1></h1>\r\n<h2>Dove Trovarci</h2>\r\n<iframe src=\"https://maps.google.it/maps?hl=it&amp;ie=UTF8&amp;q=la+giara+ragusa&amp;fb=1&amp;gl=it&amp;hq=la+giara&amp;hnear=0x131199adbb2e54a9:0x27e898810ccef3ad,Ragusa+RG&amp;cid=0,0,15304441808009745116&amp;t=m&amp;ll=36.932604,14.690416&amp;spn=0.006003,0.009141&amp;z=16&amp;iwloc=A&amp;output=embed\" frameborder=\"0\" marginwidth=\"0\" marginheight=\"0\" scrolling=\"no\" width=\"425\" height=\"350\"></iframe>", "Presentazione", "", "inherit", "closed", "open", "", "771-revision-v1", "", "", "2013-10-08 18:34:46", "2013-10-08 16:34:46", "", "771", "http://www.asdproragusa.it/771-revision-v1/", "0", "revision", "", "0");
