INSERT INTO `wp_posts` VALUES("912", "3", "2012-11-24 10:26:59", "2012-11-24 10:26:59", "Ecco arrivato il gran giorno, il <a href=\"http://www.asdproragusa.it\"><strong>Pro Ragusa</strong></a> finalmente diventa realtà e si appresta ad affrontare la sua prima <a href=\"http://www.asdproragusa.it/season/matches/?match=1\">partita ufficiale</a>.\r\n\r\nSeguono i convocati dal <strong><em>Mr. Schininà</em></strong> per l\'incontro di oggi:\r\n<ul>\r\n	<li>Distefano Maurizio</li>\r\n	<li>Perremuto Damiano</li>\r\n	<li>Cilia Luca</li>\r\n	<li>Cilia Gianni</li>\r\n	<li>Maltese Alessio</li>\r\n	<li>La Terra Gianni</li>\r\n	<li>Licitra Daniele <strong>(C)</strong></li>\r\n	<li>Vitale Salvatore</li>\r\n	<li>Leggio Emanuele</li>\r\n	<li>Saraceno Gianmarco</li>\r\n	<li>Sammito Salvatore</li>\r\n	<li>Lo Presti Antonio</li>\r\n	<li>Farruggio Gianmarco</li>\r\n	<li>Cabibbo Wladimiro</li>\r\n	<li>Lillo Mirko</li>\r\n	<li>Romeo Antonio</li>\r\n	<li>Guastella Claudio</li>\r\n	<li>Ottaviano Francesco</li>\r\n</ul>\r\n<img src=\"http://www.asdproragusa.it/wp-content/uploads/2012/11/I_Campionato-01_800x600.jpg\" alt=\"Locandina I Campionato\" width=\"453\" height=\"640\" />\r\n\r\n<strong><em>Forza Pro Ragusa.</em></strong>", "Pronti Partenza Via...", "", "trash", "open", "open", "", "pronti-partenza-via-2", "", "", "2013-10-08 18:34:46", "2013-10-08 16:34:46", "", "0", "http://www.asdproragusa.it/?p=912", "0", "post", "", "4578");
